import React from 'react'
import {NavLink} from "react-router-dom";

export const Home = () => {
    return (
        <div className="jumbotron">
            <div className="container">
                <h1 className="display-4">Интернет магазин</h1>
                <p className="lead"><b>Первый проект</b><br/>
                    Мой первый проект на React/Redux. При разработке использовались курсы по созданию интернет магазина.
                    В настоящее время структура, которая доминирует конкретно в этом приложении, мною не используется.
                    Приложение написано на React/Redux, также была использована Ramda и Bootstrap 3 (поэтому некоторые
                    стили не такие, как в Bootstrap 4)<br/>
                </p>
                <NavLink className="btn btn-primary float-right" to="/phones" exact>Открыть магазин</NavLink>
            </div>
        </div>
    )
}